    public static function getSlug(Model $model, $slug) {
        $slug = Str::slug($slug);
        $pattern = new \MongoRegex("/^" . $slug . "(-[0-9]+)?$/");
        $slugResult = $model->where('slug', $pattern)->get();
        //dd($slugResult);
        $slugCount = $slugResult->count();

        return ($slugCount > 0) ? "{$slug}-{$slugCount}" : $slug;
    }
    
    public static function getSlugForSlug(Model $model, $slug) {
        //$slug = $slug;
        $pattern = new \MongoRegex("/^" . $slug . "(-[0-9]+)?$/");
