     * @return type string 
     * @method this method returns current controller name on the basis of current route action
     */
    public static function getCurrentController($onlyName = false) {
        $currentAction = \Route::currentRouteAction();
        list($controller, $method) = explode('@', $currentAction);
        //$controller = preg_replace('/.*\\\/', '', $controller);
        $controller=explode("Controllers\\",$controller);
        $controller = $controller[1];
        $controller = $controller . '@getIndex';
        if ($onlyName) {
            $controller = str_replace('Controller@getIndex', '', $controller);
        }
        return $controller;
    }

    /**
     * 
     * @param type $array
     * @param type $key_needed 
     * @return type this method return array in same format its remove all key and values except given in 
     * $key_needed param
     * 
     * 
     */
    public static function marray_column($array, $key_needed = array()) {
        if (is_array($array) && is_array($key_needed)) {
            $return = array();
            $i = 0;
            foreach ($array as $k => $v) {
                if (is_array($v)) {
                    foreach ($v as $key => $value) {
                        if (in_array($key, $key_needed)) {
                            $return[$i][$key] = $value;
                        }
                    }
                    ++$i;
                } else {
                    if (in_array($k, $key_needed)) {
                        $return[$k] = $v;
                    }
                }
            }
            return $return;
        }
    }

