    /**
     * 
     * @param type $slugData this is elequent object 
     * @method get slug and meta data description from given object and store it in slug table
     * @note convert title field to slug if slug not found in given object ot empty
     */
    public static function saveSlug($slugData, $type = false, $script = null,$str_slug='yes') {
        $slug = new Slug;
        $slug_data = ($str_slug=='no')?$slugData->slug:str_slug($slugData->slug);
        // same method use for cron and create slug from cms
        $details=[];
        // when use this method from cron , session is empty so need to insert client and property id 
        if(isset($slugData->property_id) && !is_array($slugData->property_id) && isset($slugData->client_id) && !is_array($slugData->property_id)){
          $details['property_id']= $slugData->property_id;
          $details['client_id']=$slugData->client_id;
        }else{
             $details = clientPropertyDetails();
        }
        if (helper::isSlugExists($slug_data,$details) == false) {
            $slug->slug = $slug_data;
            $slug->full_slug = isset($slugData->full_slug) ? $slugData->full_slug : $slug_data;
            $slug->type = $type ? $type : (Helper::getCurrentController(true));
            $slug->type = strtolower($slug->type);
            if (is_array($slugData->meta)) {
                $slug->meta = ['title' => null, 'description' => null, 'keyword' => null];// $slugData->meta;
            } else {
                $slug->meta = ['title' => null, 'description' => null, 'keyword' => null];
            }
            $slug->og = ['title' => null, 'keyword' => null, 'description' => null, 'image' => null, 'url' => null];
            $slug->status = "enable";
            $slug->redirect_to = null;
            $slug->redirect_code = null;
            $slug->canonical_url = null;
            $slug->created_by = $slugData->created_by;
            $slug->updated_by = $slugData->updated_by;
//            $details = clientPropertyDetails();
//            $slug->property_id=$details['property_id'];
//            $slug->client_id=$details['client_id'];
            $slug->property_id=$details['property_id'];
            $slug->client_id=$details['client_id'];
            if ($script) {
                $slug->created_script = $script;
            }
            $slug->save();
            return true;
        } else {
            return false;
        }
    }

    /**
     * 
     * @param type $slugData elequent object
     * @return boolean 
     * @description updateFullSlug update full flug in slug table if slug is found otherwise 
     * insert new entry for it.
     */
    public static function updateFullSlug($slugData, $script = 0, $type = false,$oldSlug=false) {
        if ($slugData->slug) {
            // update full slug 
            if(!$oldSlug){
               $oldSlug= $slugData->slug;
            }   
            $slug = Slug::getSlugBySlug($oldSlug,[],true);
            if (count($slug) > 0) {
		$slug = Slug::withTrashed()->find($slug['_id']);
                $slug->full_slug = ltrim($slugData->full_slug,'/');
                $slug->slug = $slugData->slug;
                $slug->updated_by = $slugData->updated_by;
                if ($script) {
                    $slug->slug_process = $script;
                }
                $slug->save();
            } else {
                $slug = new Slug;
                $slug->full_slug =  ltrim($slugData->full_slug,'/');
                $slug->slug = $slugData->slug;
                $slug->updated_by = $slugData->updated_by;
                $slug->created_by = $slugData->updated_by;
                $slug->type = strtolower($type);
                if(isset($slugData->meta) && is_array($slugData->meta)) {
                    $slug->meta = $slugData->meta;
                } else {
                    $slug->meta = ['title' => null, 'description' => null, 'keyword' => null];
                }
                $slug->og = ['title' => null, 'keyword' => null, 'description' => null, 'image' => null, 'url' => null];
                $slug->status = "enable";
                $slug->redirect_to = null;
                $slug->redirect_code = null;
                $slug->canonical_url = null;
                // code for clientProperty
                $details=clientPropertyDetails();
                $slug->client_id=$details['client_id'];
                $slug->property_id=$details['property_id'];
                if ($script) {
                    $slug->slug_process = $script;
                }
                $slug->save();
            }
            return true;
        } else {
            return false;
        }
    }

    /**
     * 
     * @param type $slug slug string 
     * @method get meta and slug description from given object and store it in slug table
     * @note convert title field to slug if slug not found in given object ot empty
     */
    public static function isSlugExists($slug,$options=[]) {
        $slug=  trim($slug);
        if (count(Slug::getSlugBySlug($slug,[],false,$options)) == 0) {
            return false;
        } else {
            return true;
        }
    }

    /**
     * 
     * @param type $permissions result of user role 
     * @param type $extra_permissions if user select some permissions 
     * @return type array of permissions if data exist otherwise reutrn empty array
     */
    public static function filterPermissions($permissions, $extra_permissions = []) {
        if (is_array($permissions)) {
            $permissions = array_column($permissions, 'permission');
            if (is_array($extra_permissions) && count($extra_permissions)) {
                $permissions[] = $extra_permissions;
            }
            $all_permissions = array();
            foreach ($permissions as $permission) {
                $all_permissions = array_merge($all_permissions, $permission);
            }
            return $all_permissions = array_unique($all_permissions);
        } else {
            return array();
        }
    }
    
    public static function filterPermissionsProperty($currentPermissions,$permissions,$extra_permissions=[]) {
        $property=  clientPropertyDetails();
        $currentPermissions[$property['property_id']]=  self::filterPermissions($permissions,$extra_permissions);
        return $currentPermissions;
    }
    

