    public static function getStoryType($type = 'story') {
        $return = 0;
        switch ($type) {
            case 'photo-gallery':
                $return = 4;
                break;
            case 'feature-story':
                $return = 3;
                break;
            case 'live':
                $return = 3;
                break;
            case 'story':
                $return = 1;
                break;
            case 'video':
                $return = 5;
        }

        return $return;
    }

    public static function setLimitOffset($request) {
        $return['offset'] = empty($request->input('start')) ? 0 : $request->input('start');
        $return['limit'] = empty($request->input('limit')) ? 10 : $request->input('limit');
        return $return;
    }

    /**
     * 
     * @param type $stop Y-m-d  format date records pick from 
     * @param type $start Y-m-d  format date records pick till the date 
     * @return type
     */
    public static function changeDateInMongo($stop = 0, $start = 0) {


        $stopDate = strtotime(date('Y-m-d', strtotime("-" . $stop . " days")));
        $startDate = strtotime(date('Y-m-d', strtotime("-" . $start . " days", $stopDate)));


        // start date for filter    
        $return['start'] = Carbon::createFromDate(date('Y', $startDate), date('m', $startDate), date('d', $startDate));

        // till the  date   
        $return['stop'] = Carbon::createFromDate(date('Y', $stopDate), date('m', $stopDate), date('d', $stopDate));

        return $return;
    }

