     * @return type array after filter dropdown return only those city which allowed for the particular user
     */
    public static function filterLocationDDForCityUser($section_dd = false) {
        if (Auth::user()->user_type == 'normal') {
            return $section_dd;
        }
        $return_section_dd['null'] = 'Select';
        // get all location of current user
        $cities = Auth::user()->location ? (Location::getLocationByIds(Auth::user()->location)) : null;
        $cities = json_decode(json_encode($cities), true);
        // filter secion of the basis of title for location
        if (is_array($cities)) {
            foreach ($cities as $city) {
                foreach ($section_dd as $key => $val) {
                    if (strpos($val, $city['title']) !== false) {
                        $return_section_dd[$key] = $val;
                    }
                }
            }
        }
        return $return_section_dd;
    }

    /**
     * 
     * @param type $categories_dd final dropdown list
     * @return type array after filter dropdown return only those city which allowed for the particular user
     */
    public static function filterCategoryDDForCityUser($categories_dd = false) {
        if (Auth::user()->user_type == 'normal') {
            return $categories_dd;
        } else {
            $return_categories_dd = null;
            $category = explode('|||', env('CITY_CATEGORY'));
            if (is_array($category)) {
                foreach ($category as $cat) {
                    if (array_key_exists($cat, $categories_dd)) {
                        $return_categories_dd[$cat] = $categories_dd[$cat];
                    }
                }
            }
        }
        return $return_categories_dd;
    }

    /**
     * 
     * @param type $location  selecetd by users
     * @return type array after filter dropdown
     */
    public static function filterLocationCityUser($location = false) {
        if (Auth::user()->user_type == 'normal') {
            return true;
        }
        $cities = Auth::user()->location;
        $return = false;
        if (is_array($cities) && is_array($location) && count($cities) > 0 && count($location) > 0) {
            if (count($location) == count(array_intersect($location, $cities))) {
                $return = true;
            }
        }
        return $return;
    }

    public static function convertElequentObjToArray($obj = null) {
        $parts = array();
        foreach ($obj as $key => $value) {
            //var_dump($value);
            if (isset($value['_id']) && is_object($value['_id'])) {
                //echo "object";
                foreach ($value['_id'] as $val) {
                    $value['_id'] = $val;
                }
            }
            $parts[$key] = $value;
        }
    }

    /**
     * 
     * @param type $data collection data of particular id 
     * @$type controller name
     * @description for category and location 
     */
    public static function getFullSlug($data) {
        $return = '';
        if (count($data) > 0) {
            if (isset($data['parent_id']) && !empty($data['parent_id'])) {
                $data['parent_id'] = Helper::validateMongoId($data['parent_id']);
                $parentData = $data::find($data['parent_id']);
                // if parent id is not empty then full slug is same as parentslug/slug (category/subcategory) 
                $return = isset($parentData['slug']) ? '/' . $parentData['slug'] . '/' . $data['slug'] : '/' . $data['slug'];
            } elseif (isset($data['slug'])) {
                $return = $data['slug'];
            }
        }
        return ltrim($return,'/');
    }

    /**
     * 
     * @param type $data collection data of particular id 
     * @$type controller name
     */
    public static function getStoryFullSlug($data, $script = 0) {
        $return = '';
        $full_slug_url = [];
        $category_array=['crime','events','bashindey','campus'];
        if (count($data) > 0) {
            // get story type 
            if (isset($data['type']) && $data['type'] != 'story' && $data['type'] != 'feature-story' && $data['type'] != 'live') {
                $full_slug_url[] = $data['type'];
            }
            // get category full slug 
            $cat_slug = (isset($data['category']['full_slug']) && !empty($data['category']['full_slug'])) ? $data['category']['full_slug'] : '';
            if (!$cat_slug && isset($data['category']['_id'])) {
                $category = Category::find($data['category']['_id']);
                if (count($category) > 0 && (!isset($category->full_slug) || empty($category->full_slug))) {
                    $category->full_slug= $cat_slug = self::getFullSlug($category);
                    if ($script) {
                        $category->slug_process = $script;
                    }
                    $category->save();
                    Helper::updateFullSlug($category, '1', 'category');
                    //dd($category);
                } elseif (count($category) > 0 && isset($category->full_slug)) {
                    $cat_slug = $category->full_slug;
                }
            }
            //city and state have exception "city-and-states" should not be display in url if location is mentions
            if (count($data['geo_scope'])>0 && (in_array($cat_slug,$category_array) || $cat_slug=='city-and-states')) {


//                $geo_slug = isset($data['geo_scope'][0]['slug']) ? $data['geo_scope'][0]['slug'] : '';
//                if ($geo_slug != $cat_slug) {
//                    $full_slug_url[] = $geo_slug;
//                }
                ///////////////////////
                // get category full slug 
                $loc_slug = (isset($data['geo_scope'][0]['full_slug']) && !empty($data['geo_scope'][0]['full_slug'])) ? $data['geo_scope'][0]['full_slug'] : '';
                if (!$loc_slug && isset($data['geo_scope'][0]['_id'])) {
                    $location = Location::find($data['geo_scope'][0]['_id']);
                    if (count($location) > 0 && (!isset($location->full_slug) || empty($location->full_slug))) {
                        $location->full_slug=$loc_slug = self::getFullSlug($location);
                        if ($script) {
                            $location->slug_process = $script;
                        }
                        $location->save();
                        Helper::updateFullSlug($location, '1', 'location');
                        //dd($location);
                    } elseif (count($location) > 0 && isset($location->full_slug)) {
                        $loc_slug = $location->full_slug;
                    }
                }
                $full_slug_url[] = $loc_slug;
                ////////////////////
                // if category type 
                if (in_array($cat_slug,$category_array)) {
                    $full_slug_url[] = $cat_slug;
                }
            } else {
                $full_slug_url[] = $cat_slug;
            }
            $full_slug_url[] = $data['slug'];
        }
        $full_slug_url = array_filter($full_slug_url);
        $full_slug_url = is_array($full_slug_url) ? '/' . implode('/', $full_slug_url) : '';
        $full_slug_url= empty($full_slug_url) ? '' : str_replace('//', '/', $full_slug_url);
        return ltrim($full_slug_url, '/');
    }
    
